class NewsContent {
  List<Images> _images;
  String _title;
  String _content;
  String _source;
  String _ptime;
  String _docid;
  String _cover;

  List<Images> get images => _images;

  String get title => _title;

  String get content => _content;

  String get source => _source;

  String get ptime => _ptime;

  String get docid => _docid;

  String get cover => _cover;

  void setContent(c){
    _content = c;
  }

  NewsContent(
      {List<Images> images,
      String title="",
      String content="",
      String source,
      String ptime="",
      String docid,
      String cover}) {
    _images = images;
    _title = title;
    _content = content;
    _source = source;
    _ptime = ptime;
    _docid = docid;
    _cover = cover;
  }

  NewsContent.fromJson(dynamic json) {
    if (json["images"] != null) {
      _images = [];
      json["images"].forEach((v) {
        _images.add(Images.fromJson(v));
      });
    }
    _title = json["title"];
    _content = json["content"];
    _source = json["source"];
    _ptime = json["ptime"];
    _docid = json["docid"];
    _cover = json["cover"];
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    if (_images != null) {
      map["images"] = _images.map((v) => v.toJson()).toList();
    }
    map["title"] = _title;
    map["content"] = _content;
    map["source"] = _source;
    map["ptime"] = _ptime;
    map["docid"] = _docid;
    map["cover"] = _cover;
    return map;
  }
}

