class Images {
  String _position;
  String _imgSrc;
  String _size;

  String get position => _position;

  String get imgSrc => _imgSrc;

  String get size => _size;

  Images({String position, String imgSrc, String size}) {
    _position = position;
    _imgSrc = imgSrc;
    _size = size;
  }

  Images.fromJson(dynamic json) {
    _position = json["position"];
    _imgSrc = json["imgSrc"];
    _size = json["size"];
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    map["position"] = _position;
    map["imgSrc"] = _imgSrc;
    map["size"] = _size;
    return map;
  }
}
